import React, { Component } from 'react'

export class Categories extends Component {
	constructor(props) {
		super(props)
		this.state = {
      categories: [
        {
          key: "all",
          name: "All",
        },
        {
          key: "chairs",
          name: "Chairs",
        },
        {
          key: "tables",
          name: "Table",
        },
        {
          key: "sofa",
          name: "Sofa",
        },
        {
          key: "light",
          name: "Light",
        },
      ],
    };
	}


  render() {
	 return (
		 <div className='categories'>
			 {this.state.categories.map(el => (
				 <div key={el.key} onClick={this.props.ch}>
					 {el.name}
				 </div>
			 ))}
		</div>
	 )
  }
}

export default Categories
